import React, { useContext } from "react";
import { Col } from "react-bootstrap";
import Container from "react-bootstrap/Container"
import Row from "react-bootstrap/Row";
import ContextProvider from "../../utils/ContextProvider";
import MoviePane from "../moviePane/MoviePane";
import SearchBox from "../search/SearchBox";

const ListingPage = () => {

    const { allMovies } = useContext(ContextProvider);
    const numberOfMovies = allMovies.length || 0;
    const numberOfRows = Math.ceil(numberOfMovies / 3); // per Row 3 movies.
    console.log("listing", allMovies, numberOfRows, numberOfMovies);


    return (<>
        <SearchBox />
        <Container>
            {
                Array(numberOfRows).fill("").map(_ => <Row>
                    {
                        Array(3).fill("").map((_) => <MoviePane />
                        )
                    }
                </Row>
                )
            }
        </Container>
    </>
    );
}

export default ListingPage;
