import './App.scss';
import ListingPage from './components/listingPage/ListingPage';
import { createContext, useCallback, useEffect, useState } from 'react';
import { fetchMovieData } from './apis/apis';
import { STATUS_MAP } from './utils/constants';
import ContextProvider from './utils/ContextProvider';


function App() {

  const [allMovies, setAllMovies] = useState([]);

  const handleScroll = useCallback((e) => {
    console.log("handle scrolling", e, window.screen);
  }, []);

  useEffect(() => {

    document.addEventListener("scroll", handleScroll);

    return () => {
      document.removeEventListener("scroll", handleScroll);
    }
  }, []);


  const fetchData = async () => {
    const result = await fetchMovieData({ currentPage: 1 });
    console.log("data", result?.data?.page?.["content-items"]);
    try {
      if (result.status == STATUS_MAP.OK) {
        let newMovies = result?.data?.page?.["content-items"]?.content
        //newMovies = newMovies.slice(0, 3);
        console.log("new movies", newMovies);

        setAllMovies(prev => {
          return [
            ...prev,
            ...newMovies
          ]

        })
      } else throw new Error();
    } catch {
      setAllMovies([]);
    }

  }
  useEffect(() => {
    fetchData();
  }, []);
  return (
    <div className="App">
      <ContextProvider.Provider value={{
        allMovies
      }}>
        <div onScroll={(e) => handleScroll(e)}>
          <ListingPage />

        </div>
      </ContextProvider.Provider>
    </div>
  );
}

export default App;
